    										$game_type_data = explode("_", $casino_row['game_provider_type_code']);
    										if(sizeof($game_type_data) == 2){
    											if(isset($player_lists[$casino_row['player_id']]['level']['level_rate_'.strtolower($game_type_data[1])])){
