        												$bonus =  $rate * $turnover / 100;

        												if(!empty($bonus)){
        													$player_lists[$casino_row['player_id']]['is_bonus'] = true;
        													$player_lists[$casino_row['player_id']]['bonus'] += $bonus;
        													$player_lists[$casino_row['player_id']]['turnover'] += $turnover;
        												}
        											}
    											}
    											
    										}
    									}
