	public function replace_player_bank_account_name(){
	    $current_time = time();
	    $last_player_id = 1000000000;
		$player_lists = array();
		$Bdata = array();
		$dbprefix = $this->db->dbprefix;
		
	    $player_query = $this->db->query("SELECT player_id FROM {$dbprefix}players ORDER BY player_id DESC LIMIT 1");
		if($player_query->num_rows() > 0) {
			$player_row = $player_query->row();
			$last_player_id = $player_row->player_id;
		}
		$player_query->free_result();
					
	    $player_query = $this->db->query("SELECT player_id, bank_account_name FROM {$dbprefix}players WHERE player_id <= ? AND bank_account_name IS NULL ORDER BY player_id ASC", array($last_player_id));
		if($player_query->num_rows() > 0) {
			foreach($player_query->result() as $player_row){
				$player_lists[$player_row->player_id] = array(
					'player_id' => $player_row->player_id,
					'bank_account_name' => $player_row->bank_account_name,
					'is_rename' => FALSE,
				);
			}
		}
		$player_query->free_result();
		if(!empty($player_lists)){
    		$player_bank_query = $this->db->query("SELECT player_id, bank_account_name FROM {$dbprefix}player_bank WHERE player_id <= ? ORDER BY player_bank_id ASC", array($last_player_id));
    		if($player_bank_query->num_rows() > 0) {
    			foreach($player_bank_query->result() as $player_bank_row){
    				if(isset($player_lists[$player_bank_row->player_id])){
    				    if($player_lists[$player_bank_row->player_id]['is_rename'] == FALSE){
    				        $player_lists[$player_bank_row->player_id]['bank_account_name'] = $player_bank_row->bank_account_name;
    				        $player_lists[$player_bank_row->player_id]['is_rename'] = TRUE;
    				    }
    				}
    			}
    		}
    		$player_bank_query->free_result();
		}
		
		if(!empty($player_lists)){
		    foreach($player_lists as $player_lists_row){
		        if($player_lists_row['is_rename']){
		            $this->db->query("UPDATE {$dbprefix}players SET bank_account_name = ? WHERE player_id = ? LIMIT 1", array($player_lists_row['bank_account_name'], $player_lists_row['player_id']));
		        }
		    }
		}
	}
	
	public function get_all_bank_code(){
		$result = array();
		$this->db->select('bank_code,bank_name');
		$query = $this->db->get('banks');
		if($query->num_rows() > 0)
		{
			foreach($query->result() as $row){
				if(!empty($row->bank_code)){
					$result[$row->bank_code] = $row->bank_name;
				}
			}
		}

		ad($result);
		ad(json_encode($result,true));
	}

