<?phpclass Announcement_model extends CI_Model {	protected $table_announcements = 'announcements';	protected $table_announcement_lang = 'announcement_lang';		public function get_announcement_data($id = NULL)	{			$result = NULL;				$query = $this				->db				->where('announcement_id', $id)				->limit(1)				->get($this->table_announcements);				if($query->num_rows() > 0)		{			$result = $query->row_array();  		}				$query->free_result();				return $result;	}		public function add_announcement()	{			$DBdata = array(			'content' => $this->input->post('content', TRUE),			'start_date' => strtotime($this->input->post('start_date', TRUE)),			'end_date' => strtotime($this->input->post('end_date', TRUE)),			'active' => (($this->input->post('active', TRUE) == STATUS_ACTIVE) ? STATUS_ACTIVE : STATUS_INACTIVE),			'created_by' => $this->session->userdata('username'),			'created_date' => time()		);				$this->db->insert($this->table_announcements, $DBdata);				$DBdata['announcement_id'] = $this->db->insert_id();				return $DBdata;	}		public function update_announcement($id = NULL)	{			$DBdata = array(			'content' => $this->input->post('content', TRUE),			'start_date' => strtotime($this->input->post('start_date', TRUE)),			'end_date' => strtotime($this->input->post('end_date', TRUE)),			'active' => (($this->input->post('active', TRUE) == STATUS_ACTIVE) ? STATUS_ACTIVE : STATUS_INACTIVE),			'updated_by' => $this->session->userdata('username'),			'updated_date' => time()		);				$this->db->where('announcement_id', $id);		$this->db->limit(1);		$this->db->update($this->table_announcements, $DBdata);				$DBdata['announcement_id'] = $id;				return $DBdata;	}		public function delete_announcement($id = NULL)	{			$this->db->where('announcement_id', $id);		$this->db->limit(1);		$this->db->delete($this->table_announcements);	}		public function get_announcement_lang_data($id = NULL)	{			$result = NULL;				$query = $this				->db				->select('content, language_id')				->where('announcement_id', $id)				->get($this->table_announcement_lang);				if($query->num_rows() > 0)		{			foreach($query->result() as $row)			{				$result[$row->language_id] = $row->content;			}			}				$query->free_result();				return $result;	}	public function add_announcement_lang($announcement_id = NULL, $language_id = NULL)	{		$DBdata = array(			'content' => $this->input->post('announcement_name-'.$language_id, TRUE),			'announcement_id' => $announcement_id,			'language_id' => $language_id		);		$this->db->insert($this->table_announcement_lang, $DBdata);	}		public function delete_announcement_lang($id = NULL)	{			$this->db->where('announcement_id', $id);		$this->db->delete($this->table_announcement_lang);	}}