<?phpclass Banner_model extends CI_Model {	protected $table = 'banners';		public function get_banner_data($id = NULL)	{			$result = NULL;				$query = $this				->db				->where('banner_id', $id)				->limit(1)				->get($this->table);				if($query->num_rows() > 0)		{			$result = $query->row_array();  		}				$query->free_result();				return $result;	}		public function add_banner()	{			$DBdata = array(			'banner_name' => $this->input->post('banner_name', TRUE),			'web_banner_width' => $this->input->post('web_banner_width', TRUE),			'web_banner_height' => $this->input->post('web_banner_height', TRUE),			'web_banner_alt' => $this->input->post('web_banner_alt', TRUE),			'mobile_banner_width' => $this->input->post('mobile_banner_width', TRUE),			'mobile_banner_height' => $this->input->post('mobile_banner_height', TRUE),			'mobile_banner_alt' => $this->input->post('mobile_banner_alt', TRUE),			'banner_url' => $this->input->post('banner_url', TRUE),			'banner_sequence' => $this->input->post('banner_sequence', TRUE),			'language_id' => $this->input->post('language_id', TRUE),			'active' => (($this->input->post('active', TRUE) == STATUS_ACTIVE) ? STATUS_ACTIVE : STATUS_INACTIVE),			'created_by' => $this->session->userdata('username'),			'created_date' => time()		);				if(isset($_FILES['web_banner']['size']) && $_FILES['web_banner']['size'] > 0)		{			$DBdata['web_banner'] = $_FILES['web_banner']['name'];		}				if(isset($_FILES['mobile_banner']['size']) && $_FILES['mobile_banner']['size'] > 0)		{			$DBdata['mobile_banner'] = $_FILES['mobile_banner']['name'];		}				$this->db->insert($this->table, $DBdata);				$DBdata['banner_id'] = $this->db->insert_id();				return $DBdata;	}		public function update_banner($id = NULL)	{			$DBdata = array(			'banner_name' => $this->input->post('banner_name', TRUE),			'web_banner_width' => $this->input->post('web_banner_width', TRUE),			'web_banner_height' => $this->input->post('web_banner_height', TRUE),			'web_banner_alt' => $this->input->post('web_banner_alt', TRUE),			'mobile_banner_width' => $this->input->post('mobile_banner_width', TRUE),			'mobile_banner_height' => $this->input->post('mobile_banner_height', TRUE),			'mobile_banner_alt' => $this->input->post('mobile_banner_alt', TRUE),			'banner_url' => $this->input->post('banner_url', TRUE),			'banner_sequence' => $this->input->post('banner_sequence', TRUE),			'language_id' => $this->input->post('language_id', TRUE),			'active' => (($this->input->post('active', TRUE) == STATUS_ACTIVE) ? STATUS_ACTIVE : STATUS_INACTIVE),			'updated_by' => $this->session->userdata('username'),			'updated_date' => time()		);				if(isset($_FILES['web_banner']['size']) && $_FILES['web_banner']['size'] > 0)		{			$DBdata['web_banner'] = $_FILES['web_banner']['name'];		}				if(isset($_FILES['mobile_banner']['size']) && $_FILES['mobile_banner']['size'] > 0)		{			$DBdata['mobile_banner'] = $_FILES['mobile_banner']['name'];		}				$this->db->where('banner_id', $id);		$this->db->limit(1);		$this->db->update($this->table, $DBdata);				$DBdata['banner_id'] = $id;				return $DBdata;	}		public function delete_banner($id = NULL)	{			$this->db->where('banner_id', $id);		$this->db->limit(1);		$this->db->delete($this->table);	}}